// Using for...in loop to iterate over the properties of the object
for(let prop in resume) {
  console.log(prop + ': ' + resume[prop]);
}

// Using for...of loop to iterate over the array of skills
for(let skill of resume.skills) {
  console.log(skill);
}

// Using forEach loop to iterate over the array of education
resume.education.forEach(function(edu) {
  console.log(edu.degree + ' from ' + edu.institution + ' in ' + edu.year + ', GPA: ' + edu.gpa);
});

// Using for loop to iterate over the projects array
for(let i = 0; i < resume.projects.length; i++) {
  console.log('Project ' + (i+1) + ': ' + resume.projects[i].title);
}
